-- ============================================
-- NileTech Learning Website - Complete Database Schema
-- ============================================
-- 
-- This SQL file creates the COMPLETE database and all tables
-- for the NileTech Learning Website, including:
-- - Login system (users table)
-- - Learning modules and lessons
-- - User progress tracking
-- - Feedback system
-- - Mentorship system
--
-- Instructions:
-- 1. Open phpMyAdmin or MySQL command line
-- 2. Import this SQL file (it will create the database automatically)
--    OR manually: CREATE DATABASE webtech_2025A_peter_mayen; then import
-- 3. This file includes everything needed for the entire platform
-- ============================================

-- Create database if it doesn't exist
CREATE DATABASE IF NOT EXISTS webtech_2025A_peter_mayen 
    DEFAULT CHARACTER SET utf8mb4 
    DEFAULT COLLATE utf8mb4_unicode_ci;

-- Use the database
USE webtech_2025A_peter_mayen;

-- ============================================
-- Users Table (Required for Login System)
-- ============================================
-- This table stores user accounts for authentication
-- Used by: login.php, signup.php, session management
CREATE TABLE IF NOT EXISTS users (
    id INT(11) NOT NULL AUTO_INCREMENT,
    full_name VARCHAR(100) NOT NULL,
    email VARCHAR(100) NOT NULL UNIQUE,
    password VARCHAR(255) NOT NULL,
    role ENUM('user', 'admin') DEFAULT 'user',
    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
    PRIMARY KEY (id),
    INDEX idx_email (email),
    INDEX idx_role (role)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

-- ============================================
-- Modules Table
-- ============================================
CREATE TABLE IF NOT EXISTS modules (
    id INT(11) NOT NULL AUTO_INCREMENT,
    name VARCHAR(100) NOT NULL,
    slug VARCHAR(100) NOT NULL UNIQUE,
    description TEXT,
    icon VARCHAR(50),
    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
    PRIMARY KEY (id),
    INDEX idx_slug (slug)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

-- ============================================
-- Lessons Table
-- ============================================
CREATE TABLE IF NOT EXISTS lessons (
    id INT(11) NOT NULL AUTO_INCREMENT,
    module_id INT(11) NOT NULL,
    title VARCHAR(200) NOT NULL,
    slug VARCHAR(200) NOT NULL,
    content TEXT,
    lesson_order INT(11) DEFAULT 0,
    lesson_type ENUM('text', 'interactive', 'video', 'quiz') DEFAULT 'text',
    code_example TEXT,
    expected_output TEXT,
    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
    PRIMARY KEY (id),
    INDEX idx_module_id (module_id),
    INDEX idx_slug (slug),
    UNIQUE KEY unique_module_lesson (module_id, slug)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

-- Add foreign key constraint only if referenced table has correct structure
-- Uncomment and run separately if you need foreign key constraint:
-- ALTER TABLE lessons ADD CONSTRAINT fk_lessons_module FOREIGN KEY (module_id) REFERENCES modules(id) ON DELETE CASCADE;

-- ============================================
-- User Progress Table (Track lesson completion)
-- ============================================
CREATE TABLE IF NOT EXISTS user_progress (
    id INT(11) NOT NULL AUTO_INCREMENT,
    user_id INT(11) NOT NULL,
    lesson_id INT(11) NOT NULL,
    completed BOOLEAN DEFAULT FALSE,
    completed_at TIMESTAMP NULL,
    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
    PRIMARY KEY (id),
    UNIQUE KEY unique_user_lesson (user_id, lesson_id),
    INDEX idx_user_id (user_id),
    INDEX idx_lesson_id (lesson_id)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

-- Add foreign key constraints only if referenced tables have correct structure
-- Note: These will fail silently if the referenced columns don't exist
-- Uncomment and run separately if you need foreign key constraints:
-- ALTER TABLE user_progress ADD CONSTRAINT fk_user_progress_user FOREIGN KEY (user_id) REFERENCES users(id) ON DELETE CASCADE;
-- ALTER TABLE user_progress ADD CONSTRAINT fk_user_progress_lesson FOREIGN KEY (lesson_id) REFERENCES lessons(id) ON DELETE CASCADE;

-- ============================================
-- Quiz Scores Table (Track quiz attempts and scores for prerequisite checking)
-- ============================================
CREATE TABLE IF NOT EXISTS quiz_scores (
    id INT(11) NOT NULL AUTO_INCREMENT,
    user_id INT(11) NOT NULL,
    lesson_id INT(11) NOT NULL,
    score INT(11) NOT NULL,
    total_questions INT(11) NOT NULL,
    percentage DECIMAL(5,2) NOT NULL,
    passed BOOLEAN DEFAULT FALSE,
    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    PRIMARY KEY (id),
    INDEX idx_user_lesson (user_id, lesson_id),
    INDEX idx_user_id (user_id),
    INDEX idx_lesson_id (lesson_id),
    INDEX idx_passed (passed)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

