-- ============================================
-- Database Updates/Migrations for Existing Databases
-- ============================================
-- IMPORTANT: These updates are for existing databases only
-- If you're creating a NEW database, the image column is already included in the CREATE TABLE statement above
-- 
-- These statements safely update existing databases to include the image column
-- They are safe to run multiple times and will not cause errors
-- ============================================

-- Step 1: Add image column to mentorship table (if it doesn't exist)
-- This will fail gracefully if the column already exists
SET @dbname = DATABASE();
SET @tablename = 'mentorship';
SET @columnname = 'image';
SET @preparedStatement = (SELECT IF(
  (
    SELECT COUNT(*) FROM INFORMATION_SCHEMA.COLUMNS
    WHERE
      (TABLE_SCHEMA = @dbname)
      AND (TABLE_NAME = @tablename)
      AND (COLUMN_NAME = @columnname)
  ) > 0,
  'SELECT 1',
  CONCAT('ALTER TABLE ', @tablename, ' ADD COLUMN ', @columnname, ' VARCHAR(255) NULL AFTER contact')
));
PREPARE alterIfNotExists FROM @preparedStatement;
EXECUTE alterIfNotExists;
DEALLOCATE PREPARE alterIfNotExists;

-- Step 2: Add email column to mentorship table (if it doesn't exist)
SET @columnname = 'email';
SET @preparedStatement = (SELECT IF(
  (
    SELECT COUNT(*) FROM INFORMATION_SCHEMA.COLUMNS
    WHERE
      (TABLE_SCHEMA = @dbname)
      AND (TABLE_NAME = @tablename)
      AND (COLUMN_NAME = @columnname)
  ) > 0,
  'SELECT 1',
  CONCAT('ALTER TABLE ', @tablename, ' ADD COLUMN ', @columnname, ' VARCHAR(255) NULL AFTER image')
));
PREPARE alterIfNotExists FROM @preparedStatement;
EXECUTE alterIfNotExists;
DEALLOCATE PREPARE alterIfNotExists;

-- Step 3: Update Peter Mangor Buol's entry with image path
-- This safely updates only if the image is NULL or empty
UPDATE mentorship 
SET image = 'images/mentors/peter-mangor-buol.jpg'
WHERE name = 'Peter Mangor Buol' AND (image IS NULL OR image = '');

-- Step 4: Update Peter Mangor Buol's entry with email address
-- This safely updates only if the email is NULL or empty
UPDATE mentorship 
SET email = 'petermangorbuolmayen@gmail.com'
WHERE name = 'Peter Mangor Buol' AND (email IS NULL OR email = '');

-- Step 5: Update Peter Mangor Buol's contact information
UPDATE mentorship 
SET contact = '+233539933095'
WHERE name = 'Peter Mangor Buol';

